import React, { useState } from 'react';

const Chat = () => {
  const [selectedGroup, setSelectedGroup] = useState('Group 1');
  const [messages, setMessages] = useState({
    'Group 1': [
      { sender: 'User 1', content: 'Hello from Group 1!' },
      { sender: 'User 2', content: 'Hi from Group 1!' }
    ],
    'Group 2': [
      { sender: 'User 3', content: 'Hello from Group 2!' },
