    ]
  });
  const [newMessage, setNewMessage] = useState('');

  const selectGroup = (group) => {
    setSelectedGroup(group);
  };

  const sendMessage = () => {
    setMessages({
      ...messages,
      [selectedGroup]: [...messages[selectedGroup], { sender: 'You', content: newMessage }]
    });
