    if err != nil {
        fmt.Println(err)
        return
    }
    req.Header.Set("token", apiToken)
    // Send the API request and get the response
    client := &http.Client{}
    resp, err := client.Do(req)
    if err != nil {
        fmt.Println(err)
        return
    }
    defer resp.Body.Close()
    // Read the response body
    body, err := ioutil.ReadAll(resp.Body)
    if err != nil {
