const ActivityEnumerator = require('windows-activity-enumerator');

// Set the position and size of the email message area
const messageArea = {
  x: 100, // Replace with the x-coordinate of the top-left corner of the email message area
  y: 100, // Replace with the y-coordinate of the top-left corner of the email message area
  width: 800, // Replace with the width of the email message area
  height: 600, // Replace with the height of the email message area
};

// Set the amount of scrolling for each screenshot
const scrollAmount = 100; // Replace with the amount of scrolling in pixels

// Calculate the number of screenshots needed
const numScreenshots = Math.ceil(messageArea.height / scrollAmount);

// Create an ActivityEnumerator object to find the Outlook window
const ae = new ActivityEnumerator();
ae.getWindows((windows) => {
  const outlookWindow = windows.find((w) => w.path && w.path.includes('OUTLOOK.EXE'));

  if (!outlookWindow) {
    console.error('Could not find the Outlook window');
    return;
  }

  // Set the Outlook window to be the foreground window
  ae.setForegroundWindow(outlookWindow.id);

  // Wait for the window to become active before taking screenshots
  setTimeout(() => {
    // Take the first screenshot of the email message area
    screenshot().then((imageBuffer) => {
      // Crop the screenshot to the email message area
      let emailScreenshot = sharp(imageBuffer)
