          top: messageArea.y,
          width: messageArea.width,
          height: messageArea.height,
        })
        .toBuffer();

      // Scroll the email message and take additional screenshots
      for (let i = 1; i < numScreenshots; i++) {
        ae.sendInput({ type: 'keydown', key: 'down' });
