                  top: messageArea.y + topOffset,
                  width: messageArea.width,
                  height: messageArea.height - topOffset,
                })
                .toBuffer()
                .then((currentBuffer) => Buffer.concat([prevBuffer, currentBuffer]))
            );
            if (i === numScreenshots - 1) {
              // Save the full email screenshot as a PNG file
              emailScreenshot.then((emailBuffer) => {
                sharp(emailBuffer).png().toFile('email-screenshot.png', (err, info) => {
                  if (err) {
                    console.error(err);
